def ded():
    global shrek
    shrek=0
    global lasaga
    lasaga=0
    global plus
    plus = False
    global devide
    devide = False
    global multi
    multi = False
    global submis
    submis = False
def on():
    global shrek, lasaga, plus, devide, multi, submis
    shrek = 0
    lasaga = 0
    plus = False
    devide = False
    multi = False
    submis = False
    showoff.config(text="")
def off():
    quit()
