def add():
    global plus, shrek
    shrek = showoff.cget("text")
    plus = True
    showoff.config(text="")
def sub():
    global submis, shrek
    shrek = showoff.cget("text")
    submis = True
    showoff.config(text="")
def div():
    global devide, shrek
    shrek = showoff.cget("text")
    devide = True
    showoff.config(text="")
def times():
    global multi, shrek
    shrek = showoff.cget("text")
    multi = True
    showoff.config(text="")
def equal():
    global shrek
    lasaga = showoff.cget("text")
    shrek = float(shrek)
    lasaga = float(lasaga)
    if plus is True:
        gregleg = shrek + lasaga
        showoff.config(text=gregleg)
    elif submis is True:
        greenbeen = shrek - lasaga
        showoff.config(text=greenbeen)
    elif devide is True:
        america = shrek / lasaga
        showoff.config(text=america)
    elif multi is True:
        sloth = shrek * lasaga
        showoff.config(text=sloth)
def decimal():
    showoff.config(text=showoff.cget("text") + ".")

